// src/components/FeedbackDashboard.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './FeedbackDashboard.css';

const FeedbackDashboard = () => {
  const [service, setService] = useState('');
  const [stats, setStats] = useState([]);
  const [error, setError] = useState('');

  const handleServiceChange = async (e) => {
    setService(e.target.value);
  };

  // FeedbackDashboard.js (Frontend)
const fetchStats = async () => {
    try {
      const response = await axios.get(`http://localhost:5000/api/feedback/stats?service=${service}`);
      setStats(response.data);
    } catch (err) {
      setError('Error fetching feedback data.');
    }
  };
  

  useEffect(() => {
    if (service) {
      fetchStats();
    }
  }, [service]);

  return (
    <div className="feedback-dashboard-container">
      <h1>Feedback Dashboard</h1>
      <label>Select Service:</label>
      <select onChange={handleServiceChange} value={service}>
        <option value="">Select a Service</option>
        <option value="pension">Pension Service</option>
        <option value="real-estate">Real Estate Service</option>
        <option value="loan">Loan Service</option>
      </select>
      {error && <p className="error-message">{error}</p>}
      {stats.length > 0 ? (
        <>
          <h3>Feedback Stats</h3>
          {stats.map((stat, index) => (
            <div key={index}>
              <strong>{stat._id}</strong>
              <p>{stat.count} Feedbacks</p>
            </div>
          ))}
        </>
      ) : (
        <p>No data available for the selected service.</p>
      )}
    </div>
  );
};

export default FeedbackDashboard;
