import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import axios from "../axios";
import { FaUserCircle } from "react-icons/fa";
import { Bar, Doughnut } from "react-chartjs-2";
import { Chart, registerables } from "chart.js";
import './AdminDashboard.css';

Chart.register(...registerables);

const AdminDashboard = () => {
  const [adminProfile, setAdminProfile] = useState({});
  const [showProfile, setShowProfile] = useState(false);
  const [isUpdating, setIsUpdating] = useState(false);
  const [oldPassword, setOldPassword] = useState("");
  const [newPassword, setNewPassword] = useState("");
  const [newProfilePicture, setNewProfilePicture] = useState(null);
  const [servicesFeedback, setServicesFeedback] = useState({
    loan: 0,
    pension: 0,
    pensioners: 0,
    investment: 0,
  });

  const navigate = useNavigate();

  useEffect(() => {
    const fetchProfile = async () => {
      try {
        const email = localStorage.getItem("email");
        const response = await axios.get(`/admin/profile/${email}`);
        setAdminProfile({
          ...response.data,
          profilePicture: response.data.profilePicture ? `http://localhost:5000${response.data.profilePicture}` : null
        });
      } catch (error) {
        console.error("Error fetching profile:", error);
      }
    };

    const fetchServiceFeedback = async () => {
      try {
        const response = await axios.get("/admin/feedback-counts");
        setServicesFeedback(response.data);
      } catch (error) {
        console.error("Error fetching feedback counts:", error);
      }
    };

    fetchProfile();
    fetchServiceFeedback();
  }, []);

  const handleLogout = () => {
    localStorage.removeItem("token");
    localStorage.removeItem("email");
    navigate("/admin-login");
  };

  const handleProfileUpdate = async (e) => {
    e.preventDefault();
    const formData = new FormData();
    
    if (newProfilePicture) formData.append("profilePicture", newProfilePicture);
    if (oldPassword && newPassword) {
      formData.append("oldPassword", oldPassword);
      formData.append("newPassword", newPassword);
    }

    try {
      const email = localStorage.getItem("email");
      formData.append("email", email);
      const response = await axios.put("/admin/update-admin-profile", formData, {
        headers: { "Content-Type": "multipart/form-data" },
      });

      alert("Profile Updated Successfully");
      setAdminProfile(response.data);
      setIsUpdating(false);
      navigate("/dashboard");
    } catch (error) {
      alert("Update Failed: " + (error.response?.data?.message || "Server Error"));
    }
  };

  const feedbackLabels = ["Loan", "Pension", "Pensioners", "Investment"];
  const feedbackCounts = Object.values(servicesFeedback);

  return (
    <div className="dashboardContainer">
      {/* Header */}
      <div className="headerBar">
        <img src="/icons/nppf logo.webp" alt="NPPF Logo" className="service-logo" />
        <div className="profileSection">
          <button className="profileButton" onClick={() => setShowProfile(!showProfile)}>
            {adminProfile.profilePicture ? (
              <img src={adminProfile.profilePicture} alt="Profile" className="profileImage" />
            ) : (
              <FaUserCircle size={40} />
            )}
          </button>
          {showProfile && (
            <div className="profileDropdown">
              <p><strong>Email:</strong> {adminProfile.email}</p>
              <button onClick={() => setIsUpdating(true)} className="updateButton">Update</button>
              <button onClick={handleLogout} className="logoutButton">Logout</button>
            </div>
          )}
        </div>
      </div>

      {/* Sidebar */}
      <div className="sidebar">
        <h3>Services</h3>
        <ul>
          <li><a href="/loan" className="serviceLink">Loan</a></li>
          <li><a href="/pension" className="serviceLink">Pension</a></li>
          <li><a href="/pensioners" className="serviceLink">Pensioners</a></li>
          <li><a href="/investment" className="serviceLink">Investment</a></li>
        </ul>
      </div>

      {/* Main Content */}
      <div className="mainContent">
        <h2>Total Feedback for Each Service</h2>
        <div className="chartContainer">
          <Bar 
            data={{
              labels: feedbackLabels,
              datasets: [{ label: "Feedback Count", data: feedbackCounts, backgroundColor: "rgba(54, 162, 235, 0.6)" }]
            }}
          />
        </div>
        <div className="chartContainer">
          <Doughnut 
            data={{
              labels: feedbackLabels,
              datasets: [{ data: feedbackCounts, backgroundColor: ["#FF6384", "#36A2EB", "#FFCE56", "#4CAF50"] }]
            }}
          />
        </div>

        {isUpdating && (
          <div className="updateForm">
            <h3>Update Profile and Password</h3>
            <form onSubmit={handleProfileUpdate}>
              <input type="file" onChange={(e) => setNewProfilePicture(e.target.files[0])} accept="image/*" />
              <input type="password" placeholder="Old Password" value={oldPassword} onChange={(e) => setOldPassword(e.target.value)} required />
              <input type="password" placeholder="New Password" value={newPassword} onChange={(e) => setNewPassword(e.target.value)} required />
              <button type="submit">Update</button>
            </form>
          </div>
        )}
      </div>
    </div>
  );
};

export default AdminDashboard;
