import React, { useState } from "react";
import { BrowserRouter as Router, Routes, Route } from "react-router-dom";
import ServiceSelection from "./components/ServiceSelection";
import EmojiSelection from "./components/EmojiSelection";
import FeedbackForm from "./components/FeedbackForm";
import AdminLogin from "./components/AdminLogin";
import UpdatePassword from "./components/AdminUpdatePassword";

const App = () => {
  const [service_type, setServiceType] = useState('');
  const [emoji_feedback, setEmojiFeedback] = useState('');
  const [resetRequired, setResetRequired] = useState(false);

  const handleLogin = (needsReset) => {
    setResetRequired(needsReset);
  };

  return (
    <Router>
      <Routes>
        <Route path="/" element={<ServiceSelection setServiceType={setServiceType} />} />
        <Route path="/emoji" element={<EmojiSelection setEmojiFeedback={setEmojiFeedback} />} />
        <Route path="/feedback" element={<FeedbackForm service_type={service_type} emoji_feedback={emoji_feedback} />} />
        <Route path="/admin-login" element={<AdminLogin onLogin={handleLogin} />} />
        {/* Conditional route for UpdatePassword */}
        {resetRequired && <Route path="/update-password" element={<UpdatePassword />} />}
      </Routes>
    </Router>
  );
};

export default App;
