import React, { useState } from 'react';
import axios from 'axios';
import './FeedbackForm.css';

const FeedbackForm = () => {
  const [serviceType, setServiceType] = useState('');
  const [emoji, setEmoji] = useState('');
  const [textFeedback, setTextFeedback] = useState('');
  const [email, setEmail] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      await axios.post(process.env.REACT_APP_API_URL, {
        service_type: serviceType,
        emoji_feedback: emoji,
        text_feedback: (emoji === 'Bad' || emoji === 'Unsatisfactory') ? textFeedback : '', // Send text feedback only for "Bad" or "Unsatisfactory"
        email: email,
      });
      alert('Feedback submitted successfully');
      // Reset the form after submission
      setServiceType('');
      setEmoji('');
      setTextFeedback('');
      setEmail('');
    } catch (error) {
      console.error('Error submitting feedback:', error);
      alert('An error occurred while submitting your feedback.');
    }
  };

  return (
    <div className="feedback-form-container">
      <h2>Provide Your Feedback</h2>
      <form onSubmit={handleSubmit}>
        {/* Service Type Selection */}
        <label htmlFor="serviceType">Service Type:</label>
        <select
          id="serviceType"
          value={serviceType}
          onChange={(e) => setServiceType(e.target.value)}
          required
        >
          <option value="">Select Service</option>
          <option value="Pension Service">Pension Service</option>
          <option value="Real Estate Service">Real Estate Service</option>
          <option value="Loan Service">Loan Service</option>
        </select>

        {/* Emoji Selection */}
        <div className="emoji-container">
          <label>Feedback:</label>
          <div>
            <button type="button" onClick={() => setEmoji('Happy')} className={emoji === 'Happy' ? 'selected' : ''}>
              üòä Happy
            </button>
            <button type="button" onClick={() => setEmoji('Satisfactory')} className={emoji === 'Satisfactory' ? 'selected' : ''}>
              üôÇ Satisfactory
            </button>
            <button type="button" onClick={() => setEmoji('Unsatisfactory')} className={emoji === 'Unsatisfactory' ? 'selected' : ''}>
              üòê Unsatisfactory
            </button>
            <button type="button" onClick={() => setEmoji('Bad')} className={emoji === 'Bad' ? 'selected' : ''}>
              üòû Bad
            </button>
          </div>
        </div>

        {/* Text Feedback (Conditional Rendering) */}
        {(emoji === 'Bad' || emoji === 'Unsatisfactory') && (
          <>
            <label htmlFor="textFeedback">Additional Comments:</label>
            <textarea
              id="textFeedback"
              value={textFeedback}
              onChange={(e) => setTextFeedback(e.target.value)}
              maxLength={300}
              placeholder="Please provide additional feedback (max 300 characters)."
              required
            />
          </>
        )}

        {/* Email Input */}
        <label htmlFor="email">Your Email:</label>
        <input
          type="email"
          id="email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
          placeholder="Enter your email"
          required
        />

        {/* Submit Button */}
        <button type="submit">Submit Feedback</button>
      </form>
    </div>
  );
};

export default FeedbackForm;
