import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import Header from './Header'; // Import Header component
import Footer from './Footer'; // Import Footer component
import './FeedbackForm.css';

const FeedbackForm = ({ emojiFeedback }) => {
  const [textFeedback, setTextFeedback] = useState('');
  const navigate = useNavigate();

  const handleTextChange = (e) => {
    setTextFeedback(e.target.value); // Update the text feedback state
  };

  const handleSubmit = (e) => {
    e.preventDefault(); // Prevent page reload on form submission
    // Here you would send the feedback data to your backend
    console.log('Emoji Feedback:', emojiFeedback);
    console.log('Text Feedback:', textFeedback);

    // Navigate to the thank you or confirmation page after submitting
    navigate('/thank-you');
  };

  return (
    <div className="feedback-form">
      {/* Header Component */}
      <Header />

      <div className="form-container">
        <h2>Provide Your Feedback</h2>

        <form onSubmit={handleSubmit}>
          <div className="emoji-display">
            <p>Your feedback: {emojiFeedback}</p>
          </div>

          <div className="text-feedback">
            <label htmlFor="feedbackText">Your Feedback:</label>
            <textarea
              id="feedbackText"
              value={textFeedback}
              onChange={handleTextChange}
              placeholder="Please share your thoughts..."
            ></textarea>
          </div>

          <div className="submit-section">
            <button type="submit" className="submit-btn">
              Submit Feedback
            </button>
          </div>
        </form>
      </div>

      {/* Footer Component */}
      <Footer />
    </div>
  );
};

export default FeedbackForm;
