import React, { useState, useEffect } from "react";
import axios from "../axios";
import { useNavigate } from "react-router-dom";
import './update.css';

const AdminUpdatePassword = () => {
  const [newPassword, setNewPassword] = useState("");
  const [adminProfile, setAdminProfile] = useState({});
  const [profilePicture, setProfilePicture] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchProfile = async () => {
      try {
        const email = localStorage.getItem("email");
        const response = await axios.get(`/admin/profile/${email}`);
        setAdminProfile(response.data);
      } catch (error) {
        console.error("Error fetching profile:", error);
      }
    };

    fetchProfile();
  }, []);

  const handlePasswordSubmit = async (e) => {
    e.preventDefault();
    try {
      const email = localStorage.getItem("email");
      await axios.post("/admin/update-password", { email, newPassword });
      alert("Password Updated Successfully");
      navigate("/dashboard");
    } catch (error) {
      alert("Password Update Failed: " + (error.response?.data?.message || "Server Error"));
    }
  };

  const handleProfilePictureChange = (e) => {
    const file = e.target.files[0];
    setProfilePicture(file);
  };

  const handleProfileSubmit = async (e) => {
    e.preventDefault();
    const formData = new FormData();
    formData.append("profilePicture", profilePicture);

    try {
      const email = localStorage.getItem("email");
      await axios.post(`/admin/upload-profile-picture`, formData, {
        headers: {
          "Content-Type": "multipart/form-data",
        },
        params: { email },
      });
      alert("Profile Picture Updated Successfully");
      window.location.reload(); // Reload to update profile picture
    } catch (error) {
      alert("Profile Picture Update Failed: " + (error.response?.data?.message || "Server Error"));
    }
  };

  return (
    <div className="admin-update-container">
      <h2>Update Your Profile</h2>

      <div className="profile-section">
        <div className="profile-picture">
          {adminProfile.profilePicture ? (
            <img
              src={`/uploads/${adminProfile.profilePicture}`}
              alt="Profile"
              className="profile-image"
            />
          ) : (
            <div className="default-profile">
              <span>{adminProfile.email?.charAt(0)?.toUpperCase()}</span>
            </div>
          )}
        </div>

        <form onSubmit={handleProfileSubmit} className="profile-form">
          <input
            type="file"
            onChange={handleProfilePictureChange}
            accept="image/*"
            className="profile-input"
          />
          <button type="submit" className="profile-submit-btn">Update Profile Picture</button>
        </form>
      </div>

      <form onSubmit={handlePasswordSubmit} className="password-form">
        <h3>Update Password</h3>
        <input
          type="password"
          placeholder="New Password"
          onChange={(e) => setNewPassword(e.target.value)}
          required
          className="password-input"
        />
        <button type="submit" className="update-btn">Update Password</button>
      </form>
    </div>
  );
};

export default AdminUpdatePassword;
