// src/components/FeedbackForm.js
import React, { useState } from 'react';
import axios from '../axiosConfig';
import './FeedbackForm.css';

const FeedbackForm = () => {
  const [service, setService] = useState('');
  const [emoji, setEmoji] = useState('');
  const [feedback, setFeedback] = useState('');
  const [message, setMessage] = useState('');

  const handleEmojiClick = (selectedEmoji) => {
    setEmoji(selectedEmoji);
    if (selectedEmoji === 'bad' || selectedEmoji === 'unsatisfactory') {
      setFeedback(''); // Allow text feedback for these emojis
    } else {
      setFeedback(''); // Reset feedback for other emojis
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post('/feedback', {
        service,
        emoji,
        feedback,
      });
      setMessage('Feedback submitted successfully!');
      console.log(response.data);
    } catch (error) {
      setMessage('Error submitting feedback.');
      console.error(error);
    }
  };

  return (
    <div className="feedback-form-container">
  <h1>Feedback Form</h1>
  <form onSubmit={handleSubmit}>
    <div>
      <label>Service:</label>
      <select value={service} onChange={(e) => setService(e.target.value)} required>
        <option value="">Select a service</option>
        <option value="pension">Pension Service</option>
        <option value="real-estate">Real Estate Service</option>
        <option value="loan">Loan Service</option>
      </select>
    </div>
    <div>
      <label>Emoji Feedback:</label>
      <div className="emoji-container">
        <button
          className={emoji === 'happy' ? 'selected' : ''}
          onClick={() => handleEmojiClick('happy')}
          type="button"
        >
          ðŸ˜Š
        </button>
        <button
          className={emoji === 'satisfactory' ? 'selected' : ''}
          onClick={() => handleEmojiClick('satisfactory')}
          type="button"
        >
          ðŸ™‚
        </button>
        <button
          className={emoji === 'unsatisfactory' ? 'selected' : ''}
          onClick={() => handleEmojiClick('unsatisfactory')}
          type="button"
        >
          ðŸ˜•
        </button>
        <button
          className={emoji === 'bad' ? 'selected' : ''}
          onClick={() => handleEmojiClick('bad')}
          type="button"
        >
          ðŸ˜ 
        </button>
      </div>
    </div>
    {(emoji === 'unsatisfactory' || emoji === 'bad') && (
      <div>
        <label>Feedback:</label>
        <textarea
          value={feedback}
          onChange={(e) => setFeedback(e.target.value)}
          maxLength={300}
          required
        />
      </div>
    )}
    <button type="submit">Submit</button>
  </form>
  {message && <p>{message}</p>}
</div>

  );
};

export default FeedbackForm;
