import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import { toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css'; // Don't forget to import the styles
import axios from "../axios";
import { FaUserCircle } from "react-icons/fa";
import ReactSwitch from "react-switch";
import { Line, Bar, Pie } from 'react-chartjs-2'; // Import Bar and Pie chart
import { Chart as ChartJS, CategoryScale, LinearScale, PointElement, LineElement, Title, Tooltip, Legend, BarElement, ArcElement } from 'chart.js'; // Add BarElement
import './AdminDashboard.css';

// Register ChartJS components
ChartJS.register(
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend,
  BarElement,// Ensure BarElement is registered
  ArcElement
);

const AdminDashboard = () => {
  const [adminProfile, setAdminProfile] = useState({});
  const [showProfile, setShowProfile] = useState(false);
  const [isUpdating, setIsUpdating] = useState(false);
  const [oldPassword, setOldPassword] = useState("");
  const [newPassword, setNewPassword] = useState("");
  const [isPasswordCorrect, setIsPasswordCorrect] = useState(false);
  const [newProfilePicture, setNewProfilePicture] = useState(null);
  const email = localStorage.getItem("email");

  const [servicesFeedback, setServicesFeedback] = useState({
    loan: 0,
    pension: 0,
    pensioners: 0,
    investment: 0,
  });
  const [emojiFeedback, setEmojiFeedback] = useState({
    loan: { bad: 0, unsatisfactory: 0, satisfactory: 0, good: 0 },
    pension: { bad: 0, unsatisfactory: 0, satisfactory: 0, good: 0 },
    pensioners: { bad: 0, unsatisfactory: 0, satisfactory: 0, good: 0 },
    investment: { bad: 0, unsatisfactory: 0, satisfactory: 0, good: 0 },
  });
  const [showAnalytics, setShowAnalytics] = useState(false);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchProfile = async () => {
      try {
        const email = localStorage.getItem("email");
        const response = await axios.get(`/admin/profile/${email}`);
        const profilePictureUrl = response.data.profilePicture
          ? `http://localhost:5000${response.data.profilePicture}`
          : null;
        setAdminProfile({ ...response.data, profilePicture: profilePictureUrl });
      } catch (error) {
        console.error("Error fetching profile:", error);
      }
    };

    const fetchServiceFeedback = async () => {
      try {
        const response = await axios.get("/admin/feedback-counts");
        setServicesFeedback(response.data); // Directly store response in state
      } catch (error) {
        console.error("Error fetching feedback counts:", error);
      }
    };

    const fetchEmojiFeedback = async () => {
      try {
        const response = await axios.get("/admin/emoji-feedback-counts");
        setEmojiFeedback(response.data); // Directly store emoji feedback in state
      } catch (error) {
        console.error("Error fetching emoji feedback:", error);
      }
    };

    fetchProfile();
    fetchServiceFeedback();
    fetchEmojiFeedback();
  }, []);

  const handleLogout = () => {
    localStorage.removeItem("token");
    localStorage.removeItem("email");
    navigate("/admin-login");
  };

   // Check old password before allowing updates
   const verifyOldPassword = async () => {
    try {
      const response = await axios.post("http://localhost:5000/api/admin/check-old-password", { email, oldPassword });
      if (response.status === 200) {
        setIsPasswordCorrect(true);
      }
    } catch (error) {
      setIsPasswordCorrect(false);
      alert("Incorrect old password");
    }
  };

  // Handle Profile Update
  const handleProfileUpdate = async (e) => {
    e.preventDefault();
    const formData = new FormData();
    formData.append("email", email);
    formData.append("oldPassword", oldPassword);

    if (newProfilePicture) formData.append("profilePicture", newProfilePicture);
    if (newPassword.trim() !== "") formData.append("newPassword", newPassword);

    try {
      const response = await axios.put("/admin/update-admin-profile", formData, {
        headers: { "Content-Type": "multipart/form-data" },
      });

      alert(response.data.message);
      setAdminProfile((prev) => ({ ...prev, profilePicture: response.data.profilePicture }));
      setIsUpdating(false);
    } catch (error) {
      alert(error.response?.data?.message || "Update failed");
    }
  };
  // Handle toggle change
  const handleToggleChange = (checked) => {
    setShowAnalytics(checked);
  };

  // Bar chart data (Feedback count for each service)
  const barChartData = {
    labels: ['Loan', 'Pension', 'Pensioners', 'Investment'],
    datasets: [
      {
        label: 'Feedback Count',
        data: [
          servicesFeedback.loan,
          servicesFeedback.pension,
          servicesFeedback.pensioners,
          servicesFeedback.investment,
        ],
        backgroundColor: 'rgba(75,192,192,0.2)',
        borderColor: 'rgba(75,192,192,1)',
        borderWidth: 1
      },
    ],
  };

  // Pie chart data (Emoji feedback percentages)
  // const pieChartData = (service) => {
  //   const totalFeedback = Object.values(emojiFeedback[service]).reduce((a, b) => a + b, 0);
  //   const percentages = Object.keys(emojiFeedback[service]).map((emoji) => (emojiFeedback[service][emoji] / totalFeedback) * 100);

  //   return {
  //     labels: ['Bad', 'Unsatisfactory', 'Satisfactory', 'Good'],
  //     datasets: [
  //       {
  //         data: percentages,
  //         backgroundColor: ['#FF0000', '#FF8000', '#FFFF00', '#008000'],
  //       },
  //     ],
  //   };
  // };

  return (
    <div className="dashboardContainer">
      {/* Header Bar */}
      <div className="headerBar">
        <img
          src="/icons/nppf logo.webp"
          alt="NPPF Logo"
          className="service-logo"
        />
        <div className="profileSection">
          <button className="profileButton" onClick={() => setShowProfile(!showProfile)}>
            {adminProfile.profilePicture ? (
              <img src={adminProfile.profilePicture} alt="Profile" className="profileImage" />
            ) : (
              <FaUserCircle size={40} />
            )}
          </button>
          {showProfile && (
            <div className="profileDropdown">
              <p><strong>Email:</strong> {adminProfile.email}</p>
              <button onClick={() => setIsUpdating(true)} className="updateButton">Update</button>
              <button onClick={handleLogout} className="logoutButton">Logout</button>
            </div>
          )}
        </div>
      </div>

      <div className="sidebar">
        <h3>Services</h3>
        <ul>
          <li><a href="/loan" className="serviceLink">Loan</a></li>
          <li><a href="/pension" className="serviceLink">Pension</a></li>
          <li><a href="/pensioners" className="serviceLink">Pensioners</a></li>
          <li><a href="/investment" className="serviceLink">Investment</a></li>
        </ul>
      </div>

      <div className="mainContent">
        <h2>Total Feedback For Each Service</h2>

        {/* Fancy Toggle */}
        <div className="toggleSwitch">
          <span>Show Graphical Analytics: </span>
          <ReactSwitch
            checked={showAnalytics}
            onChange={handleToggleChange}
            offColor="#ccc"
            onColor="#86d3ff"
            handleDiameter={20}
            uncheckedIcon={false}
            checkedIcon={false}
          />
        </div>

        {/* Conditionally render graphical analytics */}
        {showAnalytics ? (
          <div className="graphicalAnalytics">
            {/* Render the Bar chart for feedback count */}
            <Bar data={barChartData} options={{ responsive: true }} />

            {/* Render the Pie chart for each service's emoji feedback */}
            {/* <div className="pieCharts">
              <div>
                <h4>Loan Feedback</h4>
                <Pie data={pieChartData('loan')} options={{ responsive: true }} />
              </div>
              <div>
                <h4>Pension Feedback</h4>
                <Pie data={pieChartData('pension')} options={{ responsive: true }} />
              </div>
              <div>
                <h4>Pensioners Feedback</h4>
                <Pie data={pieChartData('pensioners')} options={{ responsive: true }} />
              </div>
              <div>
                <h4>Investment Feedback</h4>
                <Pie data={pieChartData('investment')} options={{ responsive: true }} />
              </div>
            </div> */}
          </div>
        ) : (
          <div className="serviceBoxes">
            {/* Display individual service boxes with feedback count */}
            <div className="serviceBox">
              <img src="/icons/lOan.png" alt="Loan" className="serviceImage" />
              <h3>Loan</h3>
              <p>Feedback Count: {servicesFeedback.loan}</p>
            </div>
            <div className="serviceBox">
              <img src="/icons/pension and PF.png" alt="Pension" className="serviceImage" />
              <h3>Pension</h3>
              <p>Feedback Count: {servicesFeedback.pension}</p>
            </div>
            <div className="serviceBox">
              <img src="/icons/Pensioners.png" alt="Pensioners" className="serviceImage" />
              <h3>Pensioners</h3>
              <p>Feedback Count: {servicesFeedback.pensioners}</p>
            </div>
            <div className="serviceBox">
              <img src="/icons/investment porfoli.png" alt="Investment" className="serviceImage" />
              <h3>Investment</h3>
              <p>Feedback Count: {servicesFeedback.investment}</p>
            </div>
          </div>
        )}

{isUpdating && (
  <div className="modal-overlay">
    <div className="updateForm">
      <button className="closeButton" onClick={() => setIsUpdating(false)}>&times;</button>
      <h3>Update Profile</h3>
      <form onSubmit={handleProfileUpdate}>
        <label>Old Password (Required):</label>
        <input
          type="password"
          value={oldPassword}
          onChange={(e) => setOldPassword(e.target.value)}
          onBlur={verifyOldPassword}
          required
        />

        <label>New Password:</label>
        <input type="password" value={newPassword} onChange={(e) => setNewPassword(e.target.value)} disabled={!isPasswordCorrect} />

        <label>Profile Picture:</label>
        <input type="file" accept="image/*" onChange={(e) => setNewProfilePicture(e.target.files[0])} disabled={!isPasswordCorrect} />

        <button type="submit" disabled={!isPasswordCorrect} className="updateButton">Update Profile</button>
      </form>
    </div>
  </div>
)}

      </div>
    </div>
  );
};

export default AdminDashboard;
