import React, { useState, useEffect } from 'react';
import axios from '../axiosConfig';
import './FeedbackDashboard.css';

const FeedbackDashboard = () => {
  const [selectedService, setSelectedService] = useState('');
  const [feedbackStats, setFeedbackStats] = useState([]);
  const [errorMessage, setErrorMessage] = useState('');

  const services = ['pension', 'real-estate', 'loan']; // Add more services if necessary

  const fetchStats = async (service) => {
    try {
      const response = await axios.get(`/api/feedback/stats?service=${service}`);
      setFeedbackStats(response.data);
      setErrorMessage('');
    } catch (error) {
      setErrorMessage('No feedback is available for this service.');
      setFeedbackStats([]);
    }
  };

  useEffect(() => {
    if (selectedService) {
      fetchStats(selectedService);
    }
  }, [selectedService]);

  return (
    <div className="dashboard-container">
      <h1>Feedback Dashboard</h1>

      <div className="service-select">
        <label>Select Service:</label>
        <select
          value={selectedService}
          onChange={(e) => setSelectedService(e.target.value)}
          required
        >
          <option value="">Select a service</option>
          {services.map((service, index) => (
            <option key={index} value={service}>
              {service.charAt(0).toUpperCase() + service.slice(1)} Service
            </option>
          ))}
        </select>
      </div>

      {errorMessage && <p className="error-message">{errorMessage}</p>}

      {feedbackStats.length > 0 && (
        <div className="feedback-stats">
          {feedbackStats.map((stat, index) => (
            <div key={index} className="stat">
              <h3>{stat._id} Feedback</h3>
              <p>Count: {stat.count}</p>
              <h4>Feedbacks:</h4>
              <ul>
                {stat.feedbacks.map((feedback, idx) => (
                  <li key={idx}>{feedback}</li>
                ))}
              </ul>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default FeedbackDashboard;
