import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import axios from "../axios";
import { FaUserCircle } from "react-icons/fa";
import './AdminDashboard.css';

const LoanPage = () => {
  const [emojiFeedbackCounts, setEmojiFeedbackCounts] = useState({
    happy: 0,
    satisfactory: 0,
    unsatisfactory: 0,
    bad: 0,
  });

   const [selectedEmoji, setSelectedEmoji] = useState(null);
   const [feedbackDetails, setFeedbackDetails] = useState([]);
   const [adminProfile, setAdminProfile] = useState({});
   const [showProfile, setShowProfile] = useState(false);
   const [isUpdating, setIsUpdating] = useState(false);
   const [newPassword, setNewPassword] = useState("");
   const [newProfilePicture, setNewProfilePicture] = useState(null);
   const navigate = useNavigate();

  useEffect(() => {
    const fetchFeedbackCounts = async () => {
      try {
        // Adjust URL to match the backend route
        const response = await axios.get(`/feedback/pensioners/emoji-counts`); // <-- Ensure this matches the backend route
        setEmojiFeedbackCounts(response.data); // Set feedback counts for Loan service
      } catch (error) {
        console.error("Error fetching pensioners feedback counts:", error);
      }
    };

    const fetchProfile = async () => {
      try {
        const email = localStorage.getItem("email");
        const response = await axios.get(`/admin/profile/${email}`);
        const profilePictureUrl = response.data.profilePicture
          ? `http://localhost:5000${response.data.profilePicture}`
          : null;
        setAdminProfile({ ...response.data, profilePicture: profilePictureUrl });
      } catch (error) {
        console.error("Error fetching profile:", error);
      }
    };

    fetchFeedbackCounts();
    fetchProfile();
  }, []);

  const handleLogout = () => {
    localStorage.removeItem("token");
    localStorage.removeItem("email");
    navigate("/admin-login");
  };

  const handleUpdate = async (e) => {
    e.preventDefault();
    const formData = new FormData();
    if (newProfilePicture) formData.append("profilePicture", newProfilePicture);
    if (newPassword) formData.append("newPassword", newPassword);

    try {
      const email = localStorage.getItem("email");
      await axios.post(`/admin/update-profile-password`, formData, {
        headers: { "Content-Type": "multipart/form-data" },
        params: { email },
      });
      alert("Profile and Password Updated Successfully");
      setIsUpdating(false);
      navigate("/loan"); // Redirect to refresh the page
    } catch (error) {
      alert("Update Failed: " + (error.response?.data?.message || "Server Error"));
    }
  };

  
  const fetchFeedbackDetails = async (emojiType) => {
    try {
      const response = await axios.get(`/feedback/pensioners/feedback-details/${emojiType}`);
      setFeedbackDetails(response.data);
      setSelectedEmoji(emojiType);
    } catch (error) {
      console.error("Error fetching feedback details:", error);
    }
  };

  const emojiMap = {
    happy: "üòä",
    satisfactory: "üòê",
    unsatisfactory: "üò°",
    bad: "üòû"
  };

  return (
    <div className="dashboardContainer">
  <div className="headerBar">
    <img src="/icons/nppf logo.webp" alt="NPPF Logo" className="service-logo" />
    <div className="profileSection">
      <button className="profileButton" onClick={() => setShowProfile(!showProfile)}>
        {adminProfile.profilePicture ? (
          <img src={adminProfile.profilePicture} alt="Profile" className="profileImage" />
        ) : (
          <FaUserCircle size={40} />
        )}
      </button>
      {showProfile && (
        <div className="profileDropdown">
          <p><strong>Email:</strong> {adminProfile.email}</p>
          <button onClick={() => setIsUpdating(true)} className="updateButton">Update</button>
          <button onClick={handleLogout} className="logoutButton">Logout</button>
        </div>
      )}
    </div>
  </div>

  <div className="sidebar">
    <h3>Services</h3>
    <ul>
      <li><a href="/loan" className="serviceLink">Loan</a></li>
      <li><a href="/pension" className="serviceLink">Pension</a></li>
      <li><a href="/pensioners" className="serviceLink">Pensioners</a></li>
      <li><a href="/investment" className="serviceLink">Investment</a></li>
    </ul>
  </div>

  <div className="mainContent">
        <h2>Feedback Count For Pensioners Service</h2>
        <div className="serviceBoxes">
          {Object.keys(emojiMap).map((emojiType) => (
            <div
              key={emojiType}
              className={`serviceBox ${selectedEmoji === emojiType ? "selected" : ""}`}
              onClick={() => fetchFeedbackDetails(emojiType)}
              style={{ cursor: "pointer" }}
            >
              <div className="emoji">{emojiMap[emojiType]}</div>
              <h3>{emojiType.charAt(0).toUpperCase() + emojiType.slice(1)}</h3>
              <p>Feedback Count: {emojiFeedbackCounts[emojiType]}</p>
            </div>
          ))}
        </div>
      {isUpdating && (
          <div className="updateForm">
            <h3>Update Profile and Password</h3>
            <form onSubmit={handleUpdate}>
              <input
                type="file"
                onChange={(e) => setNewProfilePicture(e.target.files[0])}
                accept="image/*"
              />
              <input
                type="password"
                placeholder="New Password"
                value={newPassword}
                onChange={(e) => setNewPassword(e.target.value)}
              />
              <button type="submit">Update</button>
            </form>
          </div>
        )}
      </div>
</div>

  );
};

export default LoanPage;
