import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import axios from "../axios";
import { FaUserCircle } from "react-icons/fa";
import ReactSwitch from "react-switch";
import { Line, Bar, Pie } from 'react-chartjs-2';
import { Chart as ChartJS, CategoryScale, LinearScale, PointElement, LineElement, Title, Tooltip, Legend, BarElement, ArcElement } from 'chart.js';
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css'; // Import Toastify CSS
import './AdminDashboard.css';

ChartJS.register(
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend,
  BarElement,
  ArcElement
);

const AdminDashboard = () => {
  const [adminProfile, setAdminProfile] = useState({});
  const [showProfile, setShowProfile] = useState(false);
  const [isUpdating, setIsUpdating] = useState(false);
  const [oldPassword, setOldPassword] = useState("");
  const [newPassword, setNewPassword] = useState("");
  const [isPasswordCorrect, setIsPasswordCorrect] = useState(false);
  const [newProfilePicture, setNewProfilePicture] = useState(null);
  const [formOpen, setFormOpen] = useState(false); // Track whether the form is open
  const email = localStorage.getItem("email");

  const [servicesFeedback, setServicesFeedback] = useState({
    loan: 0,
    pension: 0,
    pensioners: 0,
    investment: 0,
  });
  const [emojiFeedback, setEmojiFeedback] = useState({
    loan: { bad: 0, unsatisfactory: 0, satisfactory: 0, good: 0 },
    pension: { bad: 0, unsatisfactory: 0, satisfactory: 0, good: 0 },
    pensioners: { bad: 0, unsatisfactory: 0, satisfactory: 0, good: 0 },
    investment: { bad: 0, unsatisfactory: 0, satisfactory: 0, good: 0 },
  });
  const [showAnalytics, setShowAnalytics] = useState(false);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchProfile = async () => {
      try {
        const email = localStorage.getItem("email");
        const response = await axios.get(`/admin/profile/${email}`);
        const profilePictureUrl = response.data.profilePicture
          ? `http://localhost:5000${response.data.profilePicture}`
          : null;
        setAdminProfile({ ...response.data, profilePicture: profilePictureUrl });
      } catch (error) {
        console.error("Error fetching profile:", error);
      }
    };

    const fetchServiceFeedback = async () => {
      try {
        const response = await axios.get("/admin/feedback-counts");
        setServicesFeedback(response.data);
      } catch (error) {
        console.error("Error fetching feedback counts:", error);
      }
    };

    const fetchEmojiFeedback = async () => {
      try {
        const response = await axios.get("/admin/emoji-feedback");
        setEmojiFeedback(response.data);
      } catch (error) {
        console.error("Error fetching emoji feedback:", error);
      }
    };

    fetchProfile();
    fetchServiceFeedback();
    fetchEmojiFeedback();
  }, [email]);

  const handleProfileClick = () => {
    if (!formOpen) {
      setShowProfile(!showProfile); // Toggle profile dropdown
    }
  };

  const handleUpdatePassword = async () => {
    if (oldPassword !== adminProfile.password) {
      setIsPasswordCorrect(false);
      toast.error("Incorrect old password!");
    } else {
      setIsPasswordCorrect(true);
      try {
        const updatedProfile = { password: newPassword, profilePicture: newProfilePicture };
        await axios.put(`/admin/update-profile/${email}`, updatedProfile);
        toast.success("Password updated successfully!");
        setIsUpdating(false);
        setFormOpen(false); // Close the form after update
      } catch (error) {
        console.error("Error updating password:", error);
        toast.error("Failed to update password.");
      }
    }
  };

  const handleCloseForm = () => {
    setFormOpen(false); // Close the form when clicking "Close"
    setShowProfile(false); // Close the profile dropdown if the form is closed
  };

  const handleFormSubmit = (e) => {
    e.preventDefault();
    handleUpdatePassword();
  };

  return (
    <div className="admin-dashboard">
      <div className="sidebar">
        <div className="logo">Admin Dashboard</div>
        <div className="service">
          <h3>Services</h3>
          {/* Service section */}
        </div>
      </div>

      <div className="main-content">
        <div className="profile-section" onClick={handleProfileClick}>
          <FaUserCircle className="profile-icon" />
          <span>Profile</span>
          {showProfile && !formOpen && (
            <div className="dropdown-menu">
              <button onClick={() => setFormOpen(true)}>Update Password</button>
              <button onClick={handleCloseForm}>Close</button>
            </div>
          )}
        </div>

        {formOpen && (
          <div className="update-form">
            <h3>Update Profile</h3>
            <form onSubmit={handleFormSubmit}>
              <div>
                <label>Old Password</label>
                <input
                  type="password"
                  value={oldPassword}
                  onChange={(e) => setOldPassword(e.target.value)}
                  required
                />
              </div>
              {isPasswordCorrect && (
                <div>
                  <label>New Password</label>
                  <input
                    type="password"
                    value={newPassword}
                    onChange={(e) => setNewPassword(e.target.value)}
                    required
                  />
                </div>
              )}
              <div>
                <label>Profile Picture</label>
                <input
                  type="file"
                  onChange={(e) => setNewProfilePicture(e.target.files[0])}
                />
              </div>
              <button type="submit" disabled={!isPasswordCorrect}>Update</button>
            </form>
            <button onClick={handleCloseForm}>Close</button>
          </div>
        )}

        <ToastContainer />
      </div>
    </div>
  );
};

export default AdminDashboard;
