// FeedbackDashboard.js

import React, { useState, useEffect } from 'react';
import axios from '../axiosConfig';  // Make sure you're using axiosConfig here
import './FeedbackDashboard.css';

const FeedbackDashboard = () => {
  const [service, setService] = useState('');
  const [stats, setStats] = useState({});
  const [message, setMessage] = useState('');

  const fetchStats = async () => {
    try {
      // Ensure the URL is correctly formatted
      const response = await axios.get(`/feedback/stats?service=${service}`);
      if (response.data.length === 0) {
        setMessage('No feedback has been given for this service.');
        setStats({});
      } else {
        setMessage('');
        setStats(response.data);
      }
    } catch (error) {
      console.error('Error fetching feedback stats:', error);
      setMessage('Error fetching feedback stats.');
    }
  };

  useEffect(() => {
    if (service) {
      fetchStats();
    }
  }, [service]);

  return (
    <div className="feedback-dashboard-container">
      <h1>Feedback Dashboard</h1>
      <div>
        <label>Select Service:</label>
        <select
          value={service}
          onChange={(e) => setService(e.target.value)}
        >
          <option value="">Select a service</option>
          <option value="pension">Pension Service</option>
          <option value="real-estate">Real Estate Service</option>
          <option value="loan">Loan Service</option>
        </select>
      </div>

      {message && <p>{message}</p>}

      {stats && (
        <div>
          {stats.some((stat) => stat._id === 'happy') && <p>ðŸ˜Š Happy: {stats.find((stat) => stat._id === 'happy')?.count}</p>}
          {stats.some((stat) => stat._id === 'satisfactory') && <p>ðŸ™‚ Satisfactory: {stats.find((stat) => stat._id === 'satisfactory')?.count}</p>}
          {stats.some((stat) => stat._id === 'unsatisfactory') && <p>ðŸ˜• Unsatisfactory: {stats.find((stat) => stat._id === 'unsatisfactory')?.count}</p>}
          {stats.some((stat) => stat._id === 'bad') && <p>ðŸ˜  Bad: {stats.find((stat) => stat._id === 'bad')?.count}</p>}
        </div>
      )}
    </div>
  );
};

export default FeedbackDashboard;
