import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import axios from "../axios";
import { FaUserCircle } from "react-icons/fa";
import "./AdminDashboard.css";

const AdminDashboard = () => {
  const [adminProfile, setAdminProfile] = useState({});
  const [showProfile, setShowProfile] = useState(false);
  const [isUpdating, setIsUpdating] = useState(false);
  const [newPassword, setNewPassword] = useState("");
  const [newProfilePicture, setNewProfilePicture] = useState(null);
  const [servicesFeedback, setServicesFeedback] = useState({
    loan: 0,
    pension: 0,
    pensioners: 0,
    investment: 0,
  });
  const [oldPassword, setOldPassword] = useState(""); // Old password entered by admin
  const [canUpdateProfile, setCanUpdateProfile] = useState(false); // State to enable fields for updating profile
  const [failedAttempts, setFailedAttempts] = useState(0); // State to track failed password attempts
  const [disableUpdate, setDisableUpdate] = useState(false); // To disable the update form after too many failed attempts
  const [isOldPasswordValid, setIsOldPasswordValid] = useState(null); // State to track old password validation
  const navigate = useNavigate();

  useEffect(() => {
    const fetchProfile = async () => {
      try {
        const email = localStorage.getItem("email");
        const response = await axios.get(`/admin/profile/${email}`);
        const profilePictureUrl = response.data.profilePicture
          ? `http://localhost:5000${response.data.profilePicture}`
          : null;
        setAdminProfile({
          ...response.data,
          profilePicture: profilePictureUrl,
        });
      } catch (error) {
        console.error("Error fetching profile:", error);
      }
    };

    const fetchServiceFeedback = async () => {
      try {
        const response = await axios.get("/admin/feedback-counts");
        setServicesFeedback(response.data); // Directly store response in state
      } catch (error) {
        console.error("Error fetching feedback counts:", error);
      }
    };

    fetchProfile();
    fetchServiceFeedback();
  }, []);

  const handleLogout = () => {
    localStorage.removeItem("token");
    localStorage.removeItem("email");
    navigate("/admin-login");
  };

  const handleOldPasswordValidation = async () => {
    try {
      const formData = new FormData();
      formData.append("email", localStorage.getItem("email"));
      formData.append("oldPassword", oldPassword);

      // Send old password to server for validation
      const response = await axios.post(
        "/admin/validate-old-password",
        formData
      );

      if (response.status === 200) {
        setIsOldPasswordValid(true);
        setFailedAttempts(0); // Reset failed attempts
      } else {
        setIsOldPasswordValid(false);
        setFailedAttempts((prev) => prev + 1);
        if (failedAttempts >= 4) {
          setDisableUpdate(true);
          setTimeout(() => setDisableUpdate(false), 60000); // Re-enable after 1 minute
          alert("Too many failed attempts. Try again after 1 minute.");
        } else {
          alert("Invalid old password.");
        }
      }
    } catch (error) {
      console.error("Error verifying old password:", error);
    }
  };

  const handleProfileUpdate = async (e) => {
    e.preventDefault();

    if (disableUpdate) {
      alert(
        "You have exceeded the allowed attempts. Please try again after 1 minute."
      );
      return;
    }

    if (!isOldPasswordValid) {
      alert("Please provide the correct old password first.");
      return;
    }

    const formDataToUpdate = new FormData();
    if (newProfilePicture)
      formDataToUpdate.append("profilePicture", newProfilePicture);
    if (newPassword && newPassword !== "") {
      formDataToUpdate.append("newPassword", newPassword);
    }

    try {
      const email = localStorage.getItem("email");
      formDataToUpdate.append("email", email);

      const response = await axios.put(
        "/admin/update-admin-profile",
        formDataToUpdate,
        {
          headers: { "Content-Type": "multipart/form-data" },
        }
      );

      alert("Profile Updated Successfully");
      setAdminProfile(response.data);

      navigate("/dashboard");
    } catch (error) {
      alert(
        "Update Failed: " + (error.response?.data?.message || "Server Error")
      );
    }
  };

  return (
    <div className="dashboardContainer">
      {/* Header Bar */}
      <div className="headerBar">
        <img
          src="/icons/nppf logo.webp"
          alt="NPPF Logo"
          className="service-logo"
        />
        <div className="profileSection">
          <button
            className="profileButton"
            onClick={() => setShowProfile(!showProfile)}
          >
            {adminProfile.profilePicture ? (
              <img
                src={adminProfile.profilePicture}
                alt="Profile"
                className="profileImage"
              />
            ) : (
              <FaUserCircle size={40} />
            )}
          </button>
          {showProfile && (
            <div className="profileDropdown">
              <p>
                <strong>Email:</strong> {adminProfile.email}
              </p>
              <button
                onClick={() => setIsUpdating(true)}
                className="updateButton"
              >
                Update
              </button>
              <button onClick={handleLogout} className="logoutButton">
                Logout
              </button>
            </div>
          )}
        </div>
      </div>

      <div className="sidebar">
        <h3>Services</h3>
        <ul>
          <li>
            <a href="/loan" className="serviceLink">
              Loan
            </a>
          </li>
          <li>
            <a href="/pension" className="serviceLink">
              Pension
            </a>
          </li>
          <li>
            <a href="/pensioners" className="serviceLink">
              Pensioners
            </a>
          </li>
          <li>
            <a href="/investment" className="serviceLink">
              Investment
            </a>
          </li>
        </ul>
      </div>

      <div className="mainContent">
        <h2>Total Feedback For Each Service</h2>
        <div className="serviceBoxes">
          <div className="serviceBox">
            <img src="/icons/lOan.png" alt="Loan" className="serviceImage" />
            <h3>Loan</h3>
            <p>Feedback Count: {servicesFeedback.loan}</p>
          </div>
          <div className="serviceBox">
            <img
              src="/icons/pension and PF.png"
              alt="Pension"
              className="serviceImage"
            />
            <h3>Pension</h3>
            <p>Feedback Count: {servicesFeedback.pension}</p>
          </div>
          <div className="serviceBox">
            <img
              src="/icons/Pensioners.png"
              alt="Pensioners"
              className="serviceImage"
            />
            <h3>Pensioners</h3>
            <p>Feedback Count: {servicesFeedback.pensioners}</p>
          </div>
          <div className="serviceBox">
            <img
              src="/icons/investment porfoli.png"
              alt="Investment"
              className="serviceImage"
            />
            <h3>Investment</h3>
            <p>Feedback Count: {servicesFeedback.investment}</p>
          </div>
        </div>

        {isUpdating && (
          <div className="updateForm">
            <h3>Update Profile and Password</h3>

            {/* New fields always visible but disabled until old password is correct */}

            <form onSubmit={handleProfileUpdate}>
              {/* Old Password Input Field */}
              <input
                type="password"
                placeholder="Enter Old Password"
                value={oldPassword}
                onChange={(e) => setOldPassword(e.target.value)}
                disabled={disableUpdate} // Disable if there are too many failed attempts
              />
              {/* Display a tick or cross based on validation */}
              {isOldPasswordValid === null ? null : isOldPasswordValid ? (
                <span>✔️</span>
              ) : (
                <span>❌</span>
              )}
              <input
                type="file"
                onChange={(e) => setNewProfilePicture(e.target.files[0])}
                accept="image/*"
                disabled={!canUpdateProfile || disableUpdate} // Disable if old password is not validated
              />
              <input
                type="password"
                placeholder="New Password"
                value={newPassword}
                onChange={(e) => setNewPassword(e.target.value)}
                disabled={!canUpdateProfile || disableUpdate} // Disable if old password is not validated
              />
              <button
                type="submit"
                disabled={!canUpdateProfile || disableUpdate}
              >
                Update
              </button>
            </form>
          </div>
        )}
      </div>
    </div>
  );
};

export default AdminDashboard;
