  const handleEmojiClick = (emoji) => {
    setEmojiFeedback(emoji); // Update state
    navigate('/feedback'); // Automatically go to next page
  };

  return (
    <div className="feedback-form-container">
      <h1>How was your experience?</h1>
      <div className="emoji-container">
        <button onClick={() => handleEmojiClick('happy')}>ðŸ˜Š</button>
