import React, { useState } from 'react';
import { ToastContainer, toast } from 'react-toastify';
import axios from '../axiosConfig'; // Correct path for axios configuration
import { useNavigate } from 'react-router-dom'; // Import useNavigate for redirection
import './FeedbackForm.css'; // Correct import path for the updated CSS

const UniqueFeedbackForm = ({ service_type, emoji_feedback }) => {
  const [text_feedback, setTextFeedback] = useState('');
  const [email, setEmail] = useState('');
  const [phone, setPhone] = useState('');
  const navigate = useNavigate(); // Initialize useNavigate

  const handleSubmit = async (e) => {
    e.preventDefault();

    // Log the values for debugging
    console.log("emoji_feedback:", emoji_feedback);
    console.log("service_type:", service_type);
    console.log("text_feedback:", text_feedback);
    console.log("email:", email);
    console.log("phone:", phone);

    if (!emoji_feedback || !service_type) {
      toast.error('Something went wrong. Please restart the process.');
      return;
    }

    try {
      // Send the feedback data to the backend
      const response = await axios.post('/feedback', {
        service_type,
        emoji_feedback,
        text_feedback,
        email,
        phone,
      });

      // Log the response for debugging
      console.log('Feedback submitted:', response.data);

      toast.success('Feedback submitted successfully!');
      
      // Redirect to the first service page after the success toast
      setTimeout(() => {
        navigate('/service'); // Change '/service' to the actual path of your first service page
      }, 3000); // Adjust delay to match the toast duration

      // Reset form fields
      setTextFeedback('');
      setEmail('');
      setPhone('');
    } catch (error) {
      toast.error('Error submitting feedback.');

      // Log error for debugging purposes
      console.error('Error:', error.response ? error.response.data : error.message);
    }
  };

  return (
    <div className="feedback-form-container">
      {/* Header Section */}
      <header>
        <img
          src="/icons/nppf logo.webp"
          alt="NPPF Logo"
          className="logo"
        />
        <h1>Welcome to NPPF Service Feedback System</h1>
      </header>

      {/* Feedback Form */}
      <form onSubmit={handleSubmit}>
        {(emoji_feedback === 'unsatisfactory' || emoji_feedback === 'bad') && (
          <div className="form-group">
            <label>Feedback:</label>
            <textarea
              value={text_feedback}
              onChange={(e) => setTextFeedback(e.target.value)}
              maxLength={300}
              required
              className="input-field"
            />
          </div>
        )}
        <div className="form-group">
          <label>Email:</label>
          <input
            type="email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            required
            className="input-field"
          />
        </div>
        <div className="form-group">
          <label>Phone Number:</label>
          <input
            type="tel"
            value={phone}
            onChange={(e) => setPhone(e.target.value)}
            required
            className="input-field"
          />
        </div>
        <button type="submit" className="submit-btn">Submit</button>
      </form>

      {/* Footer Section */}
      <footer>
        <p>Get In Touch</p>
        <p>
          1039 (Toll Free) | WhatsApp (17170884) |{" "}
          <a href="mailto:lungten.norbu@nppf.org.bt">lungten.norbu@nppf.org.bt</a>
        </p>
        <div className="locations">
          <div>Thimphu</div>
          <div>Phuentsholing</div>
        </div>
      </footer>

      <ToastContainer />
    </div>
  );
};

export default UniqueFeedbackForm;
