import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import axios from "../axios";
import { FaUserCircle } from "react-icons/fa"; // Import user profile icon
import './AdminDashboard.css';

const AdminDashboard = () => {
  const [adminProfile, setAdminProfile] = useState({});
  const [showProfile, setShowProfile] = useState(false);
  const [isUpdating, setIsUpdating] = useState(false); // State to toggle update mode
  const [newName, setNewName] = useState("");
  const [newPassword, setNewPassword] = useState("");
  const [newProfilePicture, setNewProfilePicture] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchProfile = async () => {
      try {
        const email = localStorage.getItem("email");
        const response = await axios.get(`/admin/profile/${email}`);
        
        // Prepend the server URL if needed
        const profilePictureUrl = response.data.profilePicture
          ? `http://localhost:5000${response.data.profilePicture}` // Add the full URL
          : null;

        setAdminProfile({ ...response.data, profilePicture: profilePictureUrl });
      } catch (error) {
        console.error("Error fetching profile:", error);
      }
    };

    fetchProfile();
  }, []);

  const handleLogout = () => {
    localStorage.removeItem("token");
    localStorage.removeItem("email");
    navigate("/admin-login");
  };

  const handleUpdate = async (e) => {
    e.preventDefault();
    const formData = new FormData();
    if (newProfilePicture) {
      formData.append("profilePicture", newProfilePicture);
    }
    if (newName) {
      formData.append("name", newName);
    }
    if (newPassword) {
      formData.append("newPassword", newPassword);
    }

    try {
      const email = localStorage.getItem("email");
      await axios.post(`/admin/update-profile-password`, formData, {
        headers: {
          "Content-Type": "multipart/form-data",
        },
        params: { email },
      });
      alert("Profile and Password Updated Successfully");
      setIsUpdating(false);
      navigate("/admin-dashboard"); // Navigate back to the dashboard
    } catch (error) {
      alert("Update Failed: " + (error.response?.data?.message || "Server Error"));
    }
  };

  return (
    <div style={styles.dashboardContainer}>
      <div style={styles.sidebar}>
        <h3>Services</h3>
        <ul>
          <li><a href="#loan" style={styles.serviceLink}>Loan</a></li>
          <li><a href="#pension" style={styles.serviceLink}>Pension</a></li>
          <li><a href="#pensioners" style={styles.serviceLink}>Pensioners</a></li>
          <li><a href="#investment" style={styles.serviceLink}>Investment</a></li>
        </ul>
      </div>


      <div style={styles.mainContent}>
        <h2>Admin Dashboard</h2>

        <div style={styles.profileSection}>
          <button
            style={styles.profileButton}
            onClick={() => setShowProfile(!showProfile)}
          >
            {adminProfile.profilePicture ? (
              <img
                src={adminProfile.profilePicture}
                alt="Profile"
                style={styles.profileImage}
              />
            ) : (
              <FaUserCircle size={40} />
            )}
          </button>

          {showProfile && (
            <div style={styles.profileDropdown}>
              <p>
                <strong>Email:</strong> {adminProfile.email}
              </p>
              <button onClick={() => setIsUpdating(true)} style={styles.updateButton}>
                Update
              </button>
              <button onClick={handleLogout} style={styles.logoutButton}>
                Logout
              </button>
            </div>
          )}
        </div>

        {isUpdating && (
          <div style={styles.updateForm}>
            <h3>Update Profile and Password</h3>
            <form onSubmit={handleUpdate}>

              <input
                type="file"
                onChange={(e) => setNewProfilePicture(e.target.files[0])}
                accept="image/*"
              />
              <input
                type="password"
                placeholder="New Password"
                value={newPassword}
                onChange={(e) => setNewPassword(e.target.value)}
              />
              <button type="submit">Update</button>
            </form>
          </div>
        )}
      </div>
    </div>
  );
};

const styles = {
  dashboardContainer: { display: "flex", height: "100vh" },
  sidebar: {
    width: "250px",
    background: "#2c3e50",
    color: "white",
    padding: "20px",
    height: "100vh",
  },
  mainContent: { flexGrow: 1, padding: "20px" },
  profileSection: { position: "absolute", top: "20px", right: "20px" },
  profileButton: { background: "transparent", border: "none", cursor: "pointer" },
  profileImage: { width: "40px", height: "40px", borderRadius: "50%" },
  profileDropdown: {
    position: "absolute",
    top: "50px",
    right: "0px",
    background: "white",
    border: "1px solid #ddd",
    borderRadius: "5px",
    padding: "10px",
    boxShadow: "0px 4px 6px rgba(0, 0, 0, 0.1)",
  },
  updateButton: {
    background: "#3498db",
    color: "white",
    border: "none",
    padding: "5px 10px",
    cursor: "pointer",
    width: "100%",
    marginBottom: "5px",
  },
  logoutButton: {
    background: "#e74c3c",
    color: "white",
    border: "none",
    padding: "5px 10px",
    cursor: "pointer",
    width: "100%",
  },
  updateForm: {
    padding: "20px",
    border: "1px solid #ddd",
    borderRadius: "5px",
    boxShadow: "0px 4px 6px rgba(0, 0, 0, 0.1)",
    marginTop: "20px",
  },
};

export default AdminDashboard;
