import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import axios from "../axios";
import { FaUserCircle } from "react-icons/fa"; // Import user profile icon

const AdminDashboard = () => {
  const [adminProfile, setAdminProfile] = useState({});
  const [showProfile, setShowProfile] = useState(false);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchProfile = async () => {
      try {
        const email = localStorage.getItem("email");
        const response = await axios.get(`/admin/profile/${email}`);
        
        // Prepend the server URL if needed
        const profilePictureUrl = response.data.profilePicture
          ? `http://localhost:5000${response.data.profilePicture}` // Add the full URL
          : null;

        setAdminProfile({ ...response.data, profilePicture: profilePictureUrl });
      } catch (error) {
        console.error("Error fetching profile:", error);
      }
    };

    fetchProfile();
  }, []);

  const handleLogout = () => {
    localStorage.removeItem("token");
    localStorage.removeItem("email");
    navigate("/admin-login");
  };

  return (
    <div style={styles.dashboardContainer}>
      <div style={styles.sidebar}>
        <h3>Services</h3>
        <ul>
          <li>Service 1</li>
          <li>Service 2</li>
          <li>Service 3</li>
          <li>Service 4</li>
        </ul>
      </div>

      <div style={styles.mainContent}>
        <h2>Admin Dashboard</h2>

        <div style={styles.profileSection}>
          <button style={styles.profileButton} onClick={() => setShowProfile(!showProfile)}>
            {adminProfile.profilePicture ? (
              <img src={adminProfile.profilePicture} alt="Profile" style={styles.profileImage} />
            ) : (
              <FaUserCircle size={40} />
            )}
          </button>

          {showProfile && (
            <div style={styles.profileDropdown}>
              <p><strong>Email:</strong> {adminProfile.email}</p>
              <button onClick={() => navigate("/update-password")} style={styles.updateButton}>
                Update Password
              </button>
              <button onClick={handleLogout} style={styles.logoutButton}>
                Logout
              </button>
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

const styles = {
  dashboardContainer: { display: "flex", height: "100vh" },
  sidebar: { width: "250px", background: "#2c3e50", color: "white", padding: "20px", height: "100vh" },
  mainContent: { flexGrow: 1, padding: "20px" },
  profileSection: { position: "absolute", top: "20px", right: "20px" },
  profileButton: { background: "transparent", border: "none", cursor: "pointer" },
  profileImage: { width: "40px", height: "40px", borderRadius: "50%" },
  profileDropdown: { position: "absolute", top: "50px", right: "0px", background: "white", border: "1px solid #ddd", borderRadius: "5px", padding: "10px", boxShadow: "0px 4px 6px rgba(0, 0, 0, 0.1)" },
  updateButton: { background: "#3498db", color: "white", border: "none", padding: "5px 10px", cursor: "pointer", width: "100%", marginBottom: "5px" },
  logoutButton: { background: "#e74c3c", color: "white", border: "none", padding: "5px 10px", cursor: "pointer", width: "100%" },
};

export default AdminDashboard;
