// src/components/Dashboard.js
import React, { useState, useEffect } from 'react';
import axios from '../axiosConfig';

const Dashboard = () => {
  const [service, setService] = useState('');
  const [feedbackStats, setFeedbackStats] = useState([]);
  const [error, setError] = useState(null);

  // Fetch feedback data by service
  const fetchFeedbackByService = async (selectedService) => {
    try {
      const response = await axios.get('/api/feedbackByService', {
        params: { service: selectedService }, // Pass service as query parameter
      });
      setFeedbackStats(response.data);
      setError(null);
    } catch (err) {
      setError('Error fetching feedback data.');
      console.error(err);
    }
  };

  useEffect(() => {
    if (service) {
      fetchFeedbackByService(service); // Fetch feedback when service is selected
    }
  }, [service]);

  return (
    <div className="dashboard-container">
      <h1>Feedback Dashboard</h1>
      <div>
        <label>Select Service:</label>
        <select value={service} onChange={(e) => setService(e.target.value)} required>
          <option value="">Select a service</option>
          <option value="pension">Pension Service</option>
          <option value="real-estate">Real Estate Service</option>
          <option value="loan">Loan Service</option>
        </select>
      </div>

      {error && <p className="error">{error}</p>}

      {feedbackStats.length > 0 && (
        <div>
          <h2>Feedback Statistics</h2>
          <div className="feedback-stats">
            {feedbackStats.map((stat) => (
              <div key={stat._id} className="feedback-stat">
                <h3>{stat._id} Feedback</h3>
                <p>Count: {stat.count}</p>
                <h4>Feedbacks:</h4>
                <ul>
                  {stat.feedbacks.map((feedback, index) => (
                    <li key={index}>{feedback}</li>
                  ))}
                </ul>
              </div>
            ))}
          </div>
        </div>
      )}
    </div>
  );
};

export default Dashboard;
