import React, { useState } from 'react';
import axios from '../axios';  // Axios instance configured for admin routes

const AdminLogin = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [firstLogin, setFirstLogin] = useState(false);
  const [token, setToken] = useState('');

  const handleLogin = async (e) => {
    e.preventDefault();

    try {
      const response = await axios.post('/login', { email, password });
      setToken(response.data.token);
      setFirstLogin(response.data.firstLogin);

      if (response.data.firstLogin) {
        // Prompt the admin to change their password
        alert('Please update your password.');
      } else {
        // Redirect to the dashboard or admin panel
        alert('Login successful');
      }
    } catch (error) {
      alert(error.response.data.message || 'Something went wrong');
    }
  };

  return (
    <div>
      <h2>Admin Login</h2>
      <form onSubmit={handleLogin}>
        <input
          type="email"
          placeholder="Email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
        />
        <input
          type="password"
          placeholder="Password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
        />
        <button type="submit">Login</button>
      </form>
    </div>
  );
};

export default AdminLogin;
