import React, { useState } from "react";
import axios from "../axios";
import { useNavigate } from "react-router-dom";

const AdminUpdatePassword = () => {
  const [newPassword, setNewPassword] = useState("");
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const email = localStorage.getItem("email");
      await axios.post("/admin/update-password", { email, newPassword });
      alert("Password Updated Successfully");
      navigate("/dashboard");
    } catch (error) {
      alert("Password Update Failed: " + (error.response?.data?.message || "Server Error"));
    }
  };

  return (
    <div>
      <h2>Update Password</h2>
      <form onSubmit={handleSubmit}>
        <input type="password" placeholder="New Password" onChange={(e) => setNewPassword(e.target.value)} required />
        <button type="submit">Update</button>
      </form>
    </div>
  );
};

export default AdminUpdatePassword;
