import React, { useState } from "react";
import axios from "../axios";
import { useNavigate } from "react-router-dom";

const AdminRegister = () => {
  const [adminData, setAdminData] = useState({ email: "", password: "", profilePicture: null });
  const navigate = useNavigate();

  const handleChange = (e) => {
    if (e.target.name === "profilePicture") {
      setAdminData({ ...adminData, profilePicture: e.target.files[0] });
    } else {
      setAdminData({ ...adminData, [e.target.name]: e.target.value });
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const formData = new FormData();
    formData.append("email", adminData.email);
    formData.append("password", adminData.password);
    if (adminData.profilePicture) {
      formData.append("profilePicture", adminData.profilePicture);
    }
  
    try {
      await axios.post("/admin/register", formData, {
        headers: { "Content-Type": "multipart/form-data" },
      });
      alert("Admin Registered Successfully");
      navigate("/admin-login");
    } catch (error) {
      console.error("Error during registration:", error); // Log detailed error from Axios
      alert("Registration Failed: " + (error.response?.data?.message || "Server Error"));
    }
  };
  ;

  return (
    <div>
      <h2>Admin Registration</h2>
      <form onSubmit={handleSubmit}>
        <input
          type="email"
          name="email"
          placeholder="Email"
          onChange={handleChange}
          required
        />
        <input
          type="password"
          name="password"
          placeholder="Password"
          onChange={handleChange}
          required
        />
        <input
          type="file"
          name="profilePicture"
          onChange={handleChange}
        />
        <button type="submit">Register</button>
      </form>
    </div>
  );
};

export default AdminRegister;
