import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import axios from "../axios";
import { FaUserCircle } from "react-icons/fa";
import './AdminDashboard.css';

// Import Chart.js components
import { Bar, Pie, Line, Doughnut } from 'react-chartjs-2';
import { Chart as ChartJS, CategoryScale, LinearScale, BarElement, Title, Tooltip, Legend, ArcElement, PointElement, LineElement } from 'chart.js';

ChartJS.register(
  CategoryScale,
  LinearScale,
  BarElement,
  Title,
  Tooltip,
  Legend,
  ArcElement,
  PointElement,
  LineElement
);

const AdminDashboard = () => {
  const [adminProfile, setAdminProfile] = useState({});
  const [showProfile, setShowProfile] = useState(false);
  const [isUpdating, setIsUpdating] = useState(false);
  const [newPassword, setNewPassword] = useState("");
  const [newProfilePicture, setNewProfilePicture] = useState(null);
  const [servicesFeedback, setServicesFeedback] = useState({
    loan: 0,
    pension: 0,
    pensioners: 0,
    investment: 0,
  });
  const [showGraph, setShowGraph] = useState(false); // New state for toggle
  const navigate = useNavigate();

  useEffect(() => {
    const fetchProfile = async () => {
      try {
        const email = localStorage.getItem("email");
        const response = await axios.get(`/admin/profile/${email}`);
        const profilePictureUrl = response.data.profilePicture
          ? `http://localhost:5000${response.data.profilePicture}`
          : null;
        setAdminProfile({ ...response.data, profilePicture: profilePictureUrl });
      } catch (error) {
        console.error("Error fetching profile:", error);
      }
    };

    const fetchServiceFeedback = async () => {
      try {
        const response = await axios.get("/admin/feedback-counts");
        setServicesFeedback(response.data);
      } catch (error) {
        console.error("Error fetching feedback counts:", error);
      }
    };

    fetchProfile();
    fetchServiceFeedback();
  }, []);

  const handleLogout = () => {
    localStorage.removeItem("token");
    localStorage.removeItem("email");
    navigate("/admin-login");
  };

  const handleProfileUpdate = async (e) => {
    e.preventDefault();
    const formData = new FormData();

    if (newProfilePicture) {
      formData.append("profilePicture", newProfilePicture);
    }

    if (newPassword.old && newPassword.new) {
      formData.append("oldPassword", newPassword.old);
      formData.append("newPassword", newPassword.new);
    }

    try {
      const email = localStorage.getItem("email");
      formData.append("email", email);

      const response = await axios.put("/admin/update-admin-profile", formData, {
        headers: { "Content-Type": "multipart/form-data" },
      });

      alert("Profile Updated Successfully");

      const updatedProfile = response.data;
      setAdminProfile({
        ...updatedProfile,
        profilePicture: updatedProfile.profilePicture,
        name: updatedProfile.name,
      });

      setIsUpdating(false);
      navigate("/dashboard");
    } catch (error) {
      alert("Update Failed: " + (error.response?.data?.message || "Server Error"));
    }
  };

  // Graph Data
  const chartLabels = ['Loan', 'Pension', 'Pensioners', 'Investment'];
  const chartData = [servicesFeedback.loan, servicesFeedback.pension, servicesFeedback.pensioners, servicesFeedback.investment];
  const chartColors = ['#FF6384', '#36A2EB', '#FFCE56', '#4BC0C0'];

  const barChartData = {
    labels: chartLabels,
    datasets: [
      {
        label: 'Feedback Count',
        data: chartData,
        backgroundColor: chartColors,
      },
    ],
  };

  const pieChartData = {
    labels: chartLabels,
    datasets: [
      {
        data: chartData,
        backgroundColor: chartColors,
      },
    ],
  };

  const lineChartData = {
    labels: chartLabels,
    datasets: [
      {
        label: 'Feedback Trend',
        data: chartData,
        borderColor: '#36A2EB',
        backgroundColor: 'rgba(54, 162, 235, 0.2)',
        fill: true,
        tension: 0.3,
      },
    ],
  };

  const doughnutChartData = {
    labels: chartLabels,
    datasets: [
      {
        data: chartData,
        backgroundColor: chartColors,
        hoverBackgroundColor: ['#FF6384', '#36A2EB', '#FFCE56', '#4BC0C0'],
      },
    ],
  };

  return (
    <div className="dashboardContainer">
      <div className="headerBar">
        <img
          src="/icons/nppf logo.webp"
          alt="NPPF Logo"
          className="service-logo"
        />
        <div className="profileSection">
          <button className="profileButton" onClick={() => setShowProfile(!showProfile)}>
            {adminProfile.profilePicture ? (
              <img src={adminProfile.profilePicture} alt="Profile" className="profileImage" />
            ) : (
              <FaUserCircle size={40} />
            )}
          </button>
          {showProfile && (
            <div className="profileDropdown">
              <p><strong>Email:</strong> {adminProfile.email}</p>
              <button onClick={() => setIsUpdating(true)} className="updateButton">Update</button>
              <button onClick={handleLogout} className="logoutButton">Logout</button>
            </div>
          )}
        </div>
      </div>

      <div className="sidebar">
        <h3>Services</h3>
        <ul>
          <li><a href="/loan" className="serviceLink">Loan</a></li>
          <li><a href="/pension" className="serviceLink">Pension</a></li>
          <li><a href="/pensioners" className="serviceLink">Pensioners</a></li>
          <li><a href="/investment" className="serviceLink">Investment</a></li>
        </ul>
      </div>

      <div className="mainContent">
        <h2>Total Feedback For Each Service</h2>

        {/* Toggle Switch */}
        <div className="toggleSwitch">
          <label>
            <input
              type="checkbox"
              checked={showGraph}
              onChange={() => setShowGraph(!showGraph)}
            />
            Show Graphs
          </label>
        </div>

        {/* Show service feedback counts or charts based on toggle */}
        {showGraph ? (
          <div className="chartsRow">
            <div className="chartContainer">
              <h3>Bar Chart Representation</h3>
              <Bar data={barChartData} />
            </div>
            <div className="chartContainer">
              <h3>Pie Chart Representation</h3>
              <Pie data={pieChartData} />
            </div>
          </div>
        ) : (
          <div className="serviceBoxes">
            <div className="serviceBox">
              <h4>Loan</h4>
              <p>{servicesFeedback.loan} Feedbacks</p>
            </div>
            <div className="serviceBox">
              <h4>Pension</h4>
              <p>{servicesFeedback.pension} Feedbacks</p>
            </div>
            <div className="serviceBox">
              <h4>Pensioners</h4>
              <p>{servicesFeedback.pensioners} Feedbacks</p>
            </div>
            <div className="serviceBox">
              <h4>Investment</h4>
              <p>{servicesFeedback.investment} Feedbacks</p>
            </div>
          </div>
        )}

        <div className="chartsRow">
          <div className="chartContainer">
            <h3>Line Chart Representation</h3>
            <Line data={lineChartData} />
          </div>
          <div className="chartContainer">
            <h3>Doughnut Chart Representation</h3>
            <Doughnut data={doughnutChartData} />
          </div>
        </div>

        {isUpdating && (
          <div className="updateForm">
            <h3>Update Profile and Password</h3>
            <form onSubmit={handleProfileUpdate}>
              <input type="file" onChange={(e) => setNewProfilePicture(e.target.files[0])} accept="image/*" />
              <input type="password" placeholder="New Password" value={newPassword} onChange={(e) => setNewPassword(e.target.value)} />
              <button type="submit">Update</button>
            </form>
          </div>
        )}
      </div>
    </div>
  );
};

export default AdminDashboard;
