import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './FeedbackForm.css';

const ServiceSelection = ({ setServiceType }) => {
  const navigate = useNavigate();

  const handleServiceChange = (e) => {
    const selectedService = e.target.value;
    if (selectedService) {
      setServiceType(selectedService); // Update state
      navigate('/emoji'); // Automatically go to next page
    }
  };

  return (
    <div className="feedback-form-container">
      <h1>Select a Service</h1>
      <label>Service Type:</label>
      <select onChange={handleServiceChange}>
        <option value="">Select a service</option>
        <option value="pension">Pension Service</option>
        <option value="real-estate">Real Estate Service</option>
        <option value="loan">Loan Service</option>
      </select>
    </div>
  );
};

export default ServiceSelection;
