import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import axios from "../axios";
import { FaUserCircle } from "react-icons/fa";
import './AdminDashboard.css';

const AdminDashboard = () => {
  const [adminProfile, setAdminProfile] = useState({});
  const [showProfile, setShowProfile] = useState(false);
  const [isUpdating, setIsUpdating] = useState(false);
  const [newName, setNewName] = useState("");
  const [newPassword, setNewPassword] = useState("");
  const [newProfilePicture, setNewProfilePicture] = useState(null);
  const [servicesFeedback, setServicesFeedback] = useState({
    loan: 0,
    pension: 0,
    pensioners: 0,
    investment: 0,
  });
  const navigate = useNavigate();

  useEffect(() => {
    const fetchProfile = async () => {
      try {
        const email = localStorage.getItem("email");
        const response = await axios.get(`/admin/profile/${email}`);
        const profilePictureUrl = response.data.profilePicture
          ? `http://localhost:5000${response.data.profilePicture}`
          : null;
        setAdminProfile({ ...response.data, profilePicture: profilePictureUrl });
      } catch (error) {
        console.error("Error fetching profile:", error);
      }
    };

    const fetchServiceFeedback = async () => {
      try {
        const response = await axios.get("/admin/feedback-counts");
        setServicesFeedback(response.data); // Directly store response in state
      } catch (error) {
        console.error("Error fetching feedback counts:", error);
      }
    };

    fetchProfile();
    fetchServiceFeedback();
  }, []);

  const handleLogout = () => {
    localStorage.removeItem("token");
    localStorage.removeItem("email");
    navigate("/admin-login");
  };

  const handleProfileUpdate = async (e) => {
    e.preventDefault();
    const formData = new FormData();
    if (newProfilePicture) formData.append("profilePicture", newProfilePicture);
    if (newName) formData.append("name", newName);

    try {
      const email = localStorage.getItem("email");
      await axios.put(`/admin/update-profile-picture`, formData, {
        headers: { "Content-Type": "multipart/form-data" },
        params: { email },
      });
      alert("Profile Updated Successfully");
      setIsUpdating(false);
      navigate("/admin-dashboard");
    } catch (error) {
      alert("Update Failed: " + (error.response?.data?.message || "Server Error"));
    }
  };

  const handlePasswordUpdate = async (e) => {
    e.preventDefault();
    const { oldPassword, newPassword } = newPassword;

    if (!oldPassword || !newPassword) {
      return alert("Please fill in both old and new passwords.");
    }

    try {
      const email = localStorage.getItem("email");
      await axios.put(`/admin/update-password`, { email, oldPassword, newPassword });
      alert("Password Updated Successfully");
      setIsUpdating(false);
      navigate("/admin-dashboard");
    } catch (error) {
      alert("Update Failed: " + (error.response?.data?.message || "Server Error"));
    }
  };

  return (
    <div className="dashboardContainer">
      {/* Header Bar */}
      <div className="headerBar">
        <img
          src="/icons/nppf logo.webp"
          alt="NPPF Logo"
          className="service-logo" // Apply consistent logo class
        />
        <div className="profileSection">
          <button className="profileButton" onClick={() => setShowProfile(!showProfile)}>
            {adminProfile.profilePicture ? (
              <img src={adminProfile.profilePicture} alt="Profile" className="profileImage" />
            ) : (
              <FaUserCircle size={40} />
            )}
          </button>
          {showProfile && (
            <div className="profileDropdown">
              <p><strong>Email:</strong> {adminProfile.email}</p>
              <button onClick={() => setIsUpdating(true)} className="updateButton">Update</button>
              <button onClick={handleLogout} className="logoutButton">Logout</button>
            </div>
          )}
        </div>
      </div>

      <div className="sidebar">
        <h3>Services</h3>
        <ul>
          <li><a href="/loan" className="serviceLink">Loan</a></li>
          <li><a href="/pension" className="serviceLink">Pension</a></li>
          <li><a href="/pensioners" className="serviceLink">Pensioners</a></li>
          <li><a href="/investment" className="serviceLink">Investment</a></li>
        </ul>
      </div>

      <div className="mainContent">
        <h2>Total Feedback For Each Service</h2>
        <div className="serviceBoxes">
          <div className="serviceBox">
            <img src="/icons/lOan.png" alt="Loan" className="serviceImage" />
            <h3>Loan</h3>
            <p>Feedback Count: {servicesFeedback.loan}</p>
          </div>
          <div className="serviceBox">
            <img src="/icons/pension and PF.png" alt="Pension" className="serviceImage" />
            <h3>Pension</h3>
            <p>Feedback Count: {servicesFeedback.pension}</p>
          </div>
          <div className="serviceBox">
            <img src="/icons/Pensioners.png" alt="Pensioners" className="serviceImage" />
            <h3>Pensioners</h3>
            <p>Feedback Count: {servicesFeedback.pensioners}</p>
          </div>
          <div className="serviceBox">
            <img src="/icons/investment porfoli.png" alt="Investment" className="serviceImage" />
            <h3>Investment</h3>
            <p>Feedback Count: {servicesFeedback.investment}</p>
          </div>
        </div>

        {isUpdating && (
          <div className="updateForm">
            <h3>Update Profile and Password</h3>
            <form onSubmit={handleProfileUpdate}>
              <input
                type="file"
                onChange={(e) => setNewProfilePicture(e.target.files[0])}
                accept="image/*"
              />
              <input
                type="password"
                placeholder="New Password"
                value={newPassword}
                onChange={(e) => setNewPassword(e.target.value)}
              />
              <button type="submit">Update</button>
            </form>
          </div>
        )}
      </div>
    </div>
  );
};

export default AdminDashboard;
