// FeedbackDashboard.js

import React, { useState, useEffect } from 'react';
import axios from '../axiosConfig';
import './FeedbackDashboard.css';

const FeedbackDashboard = () => {
  const [feedbackStats, setFeedbackStats] = useState([]);
  const [selectedService, setSelectedService] = useState('');
  const [error, setError] = useState('');

  const fetchFeedbackStats = async () => {
    try {
      const response = await axios.get(`/api/feedback/stats`);
      setFeedbackStats(response.data);
      setError('');
    } catch (error) {
      setError('Error fetching feedback data.');
      console.error(error);
    }
  };

  const handleServiceChange = (e) => {
    setSelectedService(e.target.value);
  };

  useEffect(() => {
    fetchFeedbackStats();
  }, []);

  return (
    <div className="feedback-dashboard-container">
      <h1>Feedback Dashboard</h1>
      <label>Select Service:</label>
      <select onChange={handleServiceChange} value={selectedService}>
        <option value="">Select a service</option>
        <option value="pension">Pension Service</option>
        <option value="real-estate">Real Estate Service</option>
        <option value="loan">Loan Service</option>
      </select>

      {error && <p className="error-message">{error}</p>}

      <h3>Feedback Stats</h3>
      {feedbackStats
        .filter(stat => stat._id.service_type === selectedService)
        .map((stat) => (
          <div key={stat._id.emoji_feedback}>
            <strong>{stat._id.emoji_feedback}:</strong> {stat.count} feedbacks
          </div>
        ))}
    </div>
  );
};

export default FeedbackDashboard;
