import React, { useState } from "react";
import axios from "../axios";

const AdminRegister = ({ onRegistered }) => {
  const [adminData, setAdminData] = useState({
    email: "",
    password: "",
    profilePicture: "",
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setAdminData({ ...adminData, [name]: value });
  };

  const handleFileChange = (e) => {
    const file = e.target.files[0];
    setAdminData({ ...adminData, profilePicture: URL.createObjectURL(file) });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post("/admin/register", adminData);
      alert(response.data.message);
      onRegistered(); // Redirect to login
    } catch (error) {
      alert("Failed to register admin: " + (error.response?.data?.message || "Server Error"));
    }
  };

  return (
    <div>
      <h2>Admin Registration</h2>
      <form onSubmit={handleSubmit}>
        <input
          type="email"
          name="email"
          placeholder="Admin Email"
          value={adminData.email}
          onChange={handleChange}
          required
        />
        <input
          type="password"
          name="password"
          placeholder="Password"
          value={adminData.password}
          onChange={handleChange}
          required
        />
        <input type="file" name="profilePicture" onChange={handleFileChange} />
        <button type="submit">Register Admin</button>
      </form>
    </div>
  );
};

export default AdminRegister;
