import React, { useState } from "react";
import axios from "../axios"; // Ensure this points to axios.js

const AdminLogin = ({ onLogin }) => {
  const [adminData, setAdminData] = useState({ email: "", password: "" });

  const handleChange = (e) => {
    setAdminData({ ...adminData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post("/admin/login", adminData);
      alert("Login Successful");
      onLogin(response.data.firstLogin); // Pass whether the admin needs to reset password
    } catch (error) {
      alert("Login Failed: " + (error.response?.data?.message || "Server Error"));
    }
  };

  return (
    <div>
      <h2>Admin Login</h2>
      <form onSubmit={handleSubmit}>
        <input type="email" name="email" placeholder="Email" value={adminData.email} onChange={handleChange} required />
        <input type="password" name="password" placeholder="Password" value={adminData.password} onChange={handleChange} required />
        <button type="submit">Login</button>
      </form>
    </div>
  );
};

export default AdminLogin;
