import React, { useState, useEffect } from "react";
import axios from "../axios";

const AdminDashboard = () => {
  const [selectedService, setSelectedService] = useState("");
  const [selectedEmoji, setSelectedEmoji] = useState("");
  const [feedbackDetails, setFeedbackDetails] = useState([]);

  const handleServiceChange = (e) => {
    setSelectedService(e.target.value);
  };

  const handleEmojiClick = (emoji) => {
    setSelectedEmoji(emoji);
  };

  useEffect(() => {
    if (selectedService && selectedEmoji) {
      const fetchFeedbackDetails = async () => {
        try {
          const response = await axios.get(`/${selectedService}/feedback/details/${selectedEmoji}`);
          setFeedbackDetails(response.data);
        } catch (error) {
          console.error("Error fetching feedback details:", error);
        }
      };
      fetchFeedbackDetails();
    }
  }, [selectedService, selectedEmoji]);

  return (
    <div>
      <select value={selectedService} onChange={handleServiceChange}>
        <option value="">Select Service</option>
        <option value="loan">Loan</option>
        <option value="pension">Pension</option>
        <option value="pensioners">Pensioners</option>
        <option value="investment">Investment</option>
      </select>

      {selectedService && (
        <div>
          <button onClick={() => handleEmojiClick("happy")}>Happy üòä</button>
          <button onClick={() => handleEmojiClick("satisfactory")}>Satisfactory üòê</button>
          <button onClick={() => handleEmojiClick("unsatisfactory")}>Unsatisfactory üò°</button>
          <button onClick={() => handleEmojiClick("bad")}>Bad üòû</button>
        </div>
      )}

      {selectedEmoji && (
        <div>
          <h3>Feedback Details for {selectedEmoji}</h3>
          <table>
            <thead>
              <tr>
                <th>Email</th>
                <th>Feedback</th>
                <th>Timestamp</th>
              </tr>
            </thead>
            <tbody>
              {feedbackDetails.map((feedback, index) => (
                <tr key={index}>
                  <td>{feedback.email}</td>
                  <td>{feedback.feedback || 'No feedback provided'}</td>
                  <td>{feedback.timestamp}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      )}
    </div>
  );
};

export default AdminDashboard;
