import React, { useState } from 'react';
import { ToastContainer, toast } from 'react-toastify';
import axios from '../axiosConfig';
import styles from './UniqueFeedbackForm.module.css'; // Import the CSS Module

const UniqueFeedbackForm = ({ service_type, emoji_feedback }) => {
  const [text_feedback, setTextFeedback] = useState('');
  const [email, setEmail] = useState('');
  const [phone, setPhone] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!emoji_feedback || !service_type) {
      toast.error('Something went wrong. Please restart the process.');
      return;
    }

    try {
      await axios.post('/feedback', {
        service_type,
        emoji_feedback,
        text_feedback,
        email,
        phone,
      });
      toast.success('Feedback submitted successfully!');
      setTextFeedback('');
      setEmail('');
      setPhone('');
    } catch (error) {
      toast.error('Error submitting feedback.');
      console.error('Error:', error.response ? error.response.data : error.message);
    }
  };

  return (
    <div className={styles.uniqueFeedbackFormContainer}>
      <header className={styles.uniqueHeader}>
        <img
          src="/icons/nppf logo.webp"
          alt="NPPF Logo"
          className={styles.uniqueLogo}
        />
        <h1 className={styles.uniqueH1}>Provide Your Feedback</h1>
      </header>

      <form onSubmit={handleSubmit} className={styles.uniqueForm}>
        {(emoji_feedback === 'unsatisfactory' || emoji_feedback === 'bad') && (
          <div className={styles.uniqueFormGroup}>
            <label className={styles.uniqueLabel}>Feedback:</label>
            <textarea
              value={text_feedback}
              onChange={(e) => setTextFeedback(e.target.value)}
              maxLength={300}
              required
              className={styles.uniqueInputField}
            />
          </div>
        )}
        <div className={styles.uniqueFormGroup}>
          <label className={styles.uniqueLabel}>Email:</label>
          <input
            type="email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            required
            className={styles.uniqueInputField}
          />
        </div>
        <div className={styles.uniqueFormGroup}>
          <label className={styles.uniqueLabel}>Phone Number:</label>
          <input
            type="tel"
            value={phone}
            onChange={(e) => setPhone(e.target.value)}
            required
            className={styles.uniqueInputField}
          />
        </div>
        <button type="submit" className={styles.uniqueSubmitBtn}>Submit</button>
      </form>

      <footer className={styles.uniqueFooter}>
        <p className={styles.uniqueFooterText}>Get In Touch</p>
        <p>
          1039 (Toll Free) | WhatsApp (17170884) |{" "}
          <a href="mailto:lungten.norbu@nppf.org.bt">lungten.norbu@nppf.org.bt</a>
        </p>
        <div className={styles.uniqueLocations}>
          <div className={styles.uniqueLocation}>Thimphu</div>
          <div className={styles.uniqueLocation}>Phuentsholing</div>
        </div>
      </footer>

      <ToastContainer />
    </div>
  );
};

export default UniqueFeedbackForm;
